}

import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import './ManualQuestionForm.css';
import './common.css';

const ManualQuestionForm = ({ topics, onQuestionCreated, onCancel }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    question: '',
    options: ['', '', '', ''],
    correctIndex: 0,
    selectedTopic: topics[0] || ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  const handleOptionChange = (idx, value) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.map((opt, i) => i === idx ? value : opt)
    }));
  };

  const validateForm = () => {
    if (!formData.question.trim()) {
      return 'La pregunta es requerida';
    }
    if (formData.options.some(opt => !opt.trim())) {
      return 'Todas las opciones son requeridas';
    }
    if (!user?.getIdToken) {
      return 'Debes iniciar sesi√≥n para crear preguntas';
    }
    return '';
  };

  useEffect(() => {
    const validationError = validateForm();
    setError(validationError);
  }, [formData, user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setLoading(true);
    setError('');
    setSuccessMessage('');

    try {
      const apiBase = import.meta.env.VITE_API_URL || 'http://localhost:5000';
      const token = await user.getIdToken();

      const payload = {
        text: formData.question,
        options: formData.options,
        correctAnswerIndex: formData.correctIndex,
        category: formData.selectedTopic,
        explanation: ''
      };

      const response = await fetch(`${apiBase}/api/questions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(payload)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Error al guardar la pregunta');
      }

      setSuccessMessage('¬°Pregunta guardada correctamente!');

      if (onQuestionCreated) {
        onQuestionCreated(data.question || { ...payload });
      }
    } catch (err) {
      console.error('Error al guardar la pregunta:', err);
      setError(err.message || 'Error de conexi√≥n');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form className="manual-question-form" onSubmit={handleSubmit}>
      <h3>Escribe tu pregunta</h3>

      {error && <div className="error-message">{error}</div>}
      {successMessage && <div className="success-message">{successMessage}</div>}

      <div className={`form-group ${loading ? 'disabled' : ''}`}>
        <label>
          Tema:
          <select 
            value={formData.selectedTopic} 
            onChange={e => setFormData(prev => ({ ...prev, selectedTopic: e.target.value }))}
            disabled={loading}
          >
            {topics.map(t => <option key={t} value={t}>{t}</option>)}
          </select>
        </label>
      </div>

      <div className={`form-group ${loading ? 'disabled' : ''}`}>
        <label>
          Pregunta:
          <input
            type="text"
            value={formData.question}
            onChange={e => setFormData(prev => ({ ...prev, question: e.target.value }))}
            disabled={loading}
            required
          />
        </label>
      </div>

      <div className={`form-group ${loading ? 'disabled' : ''}`}>
        <label>Opciones:</label>
        <div className="manual-options-list">
          {formData.options.map((opt, idx) => (
            <div key={idx} className="manual-option-row">
              <input
                type="text"
                value={opt}
                onChange={e => handleOptionChange(idx, e.target.value)}
                disabled={loading}
                required
                placeholder={`Opci√≥n ${idx + 1}`}
              />
              <label className="radio-label">
                <input
                  type="radio"
                  name="correctOption"
                  checked={formData.correctIndex === idx}
                  onChange={() => setFormData(prev => ({ ...prev, correctIndex: idx }))}
                  disabled={loading}
                />
                <span>Correcta</span>
              </label>
            </div>
          ))}
        </div>
      </div>

      <div className="manual-question-actions">
        <button 
          type="submit" 
          className="btn btn-primary" 
          disabled={loading || !!error}
        >
          {loading ? (
            <>
              <div className="loading-indicator" />
              <span style={{ marginLeft: '8px' }}>Guardando...</span>
            </>
          ) : 'Guardar'}
        </button>
        <button 
          type="button" 
          className="btn btn-secondary" 
          onClick={onCancel} 
          disabled={loading}
        >
          Atr√°s
        </button>
      </div>
    </form>
  );
};

export default ManualQuestionForm;

import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';

export default function ProtectedRoute({ children, adminOnly }) {
  const { user, loading } = useAuth();

  if (loading) return null;
  if (!user) return <Navigate to="/login" replace />;
  if (adminOnly && user.email !== 'admin@example.com') return <Navigate to="/dashboard" replace />;
  return children;
}

import React from 'react';
import './AIQuestionGenerator.css';

export default function Question({ question, options, onSelect, selected }) {
  return (
    <div className="question-card">
      <h2 className="question-title">{question}</h2>
      <div className="options-list">
        {options.map((opt, idx) => (
          <button
            key={idx}
            className={`option-btn${selected === idx ? ' selected' : ''}`}
            onClick={() => onSelect(idx)}
            disabled={selected !== null}
          >
            <span className="option-label">{String.fromCharCode(65 + idx)})</span> {opt}
          </button>
        ))}
      </div>
    </div>
  );
}

import React from 'react';
import './Ranking.css';

export default function Ranking({ players }) {
  const sortedPlayers = [...players].sort((a, b) => b.score - a.score);

  const getRankIcon = (index) => {
    switch (index) {
      case 0: return 'ü•á';
      case 1: return 'ü•à';
      case 2: return 'ü•â';
      default: return `#${index + 1}`;
    }
  };

  const getRankClass = (index) => {
    switch (index) {
      case 0: return 'first';
      case 1: return 'second';
      case 2: return 'third';
      default: return '';
    }
  };

  return (
    <div className="mx-auto w-full max-w-xl">
      <h3 className="mb-4 font-bold text-xl">üèÜ Tabla de posiciones</h3>
      <div className="flex flex-col bg-white/3 rounded-md divide-y divide-white/5 overflow-hidden">
        {sortedPlayers.map((player, index) => (
          <div key={player.uid} className={`flex items-center gap-4 p-3 ${getRankClass(index)}`}>
            <div className="flex justify-center items-center bg-white/5 rounded-full w-10 h-10 text-lg">{getRankIcon(index)}</div>
            <div className="flex-1">
              <div className="font-medium">{player.displayName || player.email}</div>
              <div className="text-white/80 text-sm">{player.score} puntos</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

import React, { useEffect, useState, useRef } from 'react';
import './Timer.css';

export default function Timer({ seconds = 20, onEnd, onTick }) {
  const [time, setTime] = useState(seconds);
  const intervalRef = useRef();

  useEffect(() => {
    setTime(seconds);
  }, [seconds]);

  useEffect(() => {
    if (time === 0) {
      if (onEnd) onEnd();
      return;
    }
    intervalRef.current = setInterval(() => {
      setTime(t => t - 1);
    }, 1000);
    return () => clearInterval(intervalRef.current);
  }, [time, onEnd]);

  useEffect(() => {
    if (onTick) onTick(time);
  }, [time, onTick]);

  const getTimerColor = () => {
    if (time <= 3) return 'critical';
    if (time <= 6) return 'warning';
    return 'normal';
  };

  return (
    <div className={`timer ${getTimerColor()}`}>
      <div className="timer-circle">
        <span className="timer-text">{time}</span>
      </div>
      <span className="timer-label">segundos restantes</span>
    </div>
  );
}

import React, { useEffect, useState } from 'react';
import mockDb from '../services/mockDb';

const emptyForm = {
  text: '',
  options: ['', '', '', ''],
  correctAnswerIndex: 0,
  category: '',
  explanation: ''
};

export default function AdminPage() {
  const [questions, setQuestions] = useState([]);
  const [form, setForm] = useState(emptyForm);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchQuestions = async () => {
    setLoading(true);
    const snap = await mockDb.collection('questions').get();
    setQuestions(snap.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    setLoading(false);
  };

  useEffect(() => { fetchQuestions(); }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };
  const handleOptionChange = (idx, value) => {
    setForm(f => {
      const options = [...f.options];
      options[idx] = value;
      return { ...f, options };
    });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await mockDb.collection('questions').doc(editingId).update(form);
    } else {
      await mockDb.collection('questions').add(form);
    }
    setForm(emptyForm);
    setEditingId(null);
    fetchQuestions();
  };
  const handleEdit = (q) => {
    setForm(q);
    setEditingId(q.id);
  };
  const handleDelete = async (id) => {
    await mockDb.collection('questions').doc(id).delete();
    fetchQuestions();
  };

  return (
    <div>
      <h2>Admin Panel</h2>
      <form onSubmit={handleSubmit}>
        <input name="text" value={form.text} onChange={handleChange} placeholder="Question text" required />
        <input name="category" value={form.category} onChange={handleChange} placeholder="Category" required />
        <input name="explanation" value={form.explanation} onChange={handleChange} placeholder="Explanation" required />
        {form.options.map((opt, idx) => (
          <div key={idx}>
            <input
              value={opt}
              onChange={e => handleOptionChange(idx, e.target.value)}
              placeholder={`Option ${idx + 1}`}
              required
            />
            <input
              type="radio"
              name="correctAnswerIndex"
              checked={form.correctAnswerIndex === idx}
              onChange={() => setForm(f => ({ ...f, correctAnswerIndex: idx }))}
            /> Correct
          </div>
        ))}
        <button type="submit">{editingId ? 'Update' : 'Add'} Question</button>
        {editingId && <button type="button" onClick={() => { setForm(emptyForm); setEditingId(null); }}>Cancel</button>}
      </form>
      <hr />
      <h3>Questions</h3>
      {loading ? <p>Loading...</p> : (
        <table border="1">
          <thead>
            <tr>
              <th>Text</th>
              <th>Category</th>
              <th>Options</th>
              <th>Correct</th>
              <th>Explanation</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {questions.map(q => (
              <tr key={q.id}>
                <td>{q.text}</td>
                <td>{q.category}</td>
                <td>{q.options.join(', ')}</td>
                <td>{q.options[q.correctAnswerIndex]}</td>
                <td>{q.explanation}</td>
                <td>
                  <button onClick={() => handleEdit(q)}>Edit</button>
                  <button onClick={() => handleDelete(q.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

