            {loading ? 'Enviando...' : 'Enviar correo de restablecimiento'}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            <Link to="/login">Volver al inicio de sesi√≥n</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

import React, { useEffect, useState } from 'react';
import { useAuth } from '../AuthContext';
import './ProfilePage.css';

export default function ProfilePage() {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [apiError, setApiError] = useState('');
  const [apiRaw, setApiRaw] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchProfile() {
      if (!user) return;
      let statsData = null;
      try {
  const apiBase = import.meta.env.VITE_API_URL;
        // Obtener el token de Firebase
        const token = user && (await user.getIdToken());
        const statsRes = await fetch(`${apiBase}/api/users/me/stats?uid=${user.uid}`,
          {
            headers: {
              'Authorization': token ? `Bearer ${token}` : '',
              'Content-Type': 'application/json'
            }
          }
        );
        if (statsRes.ok) {
          statsData = await statsRes.json();
          setApiRaw(statsData);
        } else {
          setApiError(`Error HTTP: ${statsRes.status}`);
        }
      } catch (err) {
        setApiError('Error de conexi√≥n: ' + err.message);
      }
      setStats(statsData?.stats || null);
      setLoading(false);
    }
    fetchProfile();
  }, [user]);

  if (loading) return null;
  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img
            src={`https://api.dicebear.com/7.x/identicon/svg?seed=${user?.uid || 'user'}`}
            alt="avatar"
            className="profile-avatar"
            loading="lazy"
            decoding="async"
          />
          <div>
            <h2>{user?.displayName || user?.email}</h2>
            <span className="profile-uid">UID: {user?.uid}</span>
          </div>
        </div>
        <div className="profile-stats">
          <h3>Estad√≠sticas</h3>
          {apiError && (
            <div className="error-message">{apiError}</div>
          )}
          {stats ? (
            <div className="stats-grid">
              <div className="stat-card">
                <span className="stat-label">Partidas jugadas</span>
                <span className="stat-value">{stats.gamesPlayed}</span>
              </div>
              <div className="stat-card">
                <span className="stat-label">Victorias</span>
                <span className="stat-value">{stats.wins}</span>
              </div>
              <div className="stat-card">
                <span className="stat-label">Respuestas correctas</span>
                <span className="stat-value">{stats.correctAnswers}</span>
              </div>
            </div>
          ) : (
            <>
              <p>No hay estad√≠sticas.</p>
              {apiRaw && (
                <details>
                  <summary>Respuesta de la API</summary>
                  <pre>{JSON.stringify(apiRaw, null, 2)}</pre>
                </details>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { auth, db } from '../services/firebase';
import { createUserWithEmailAndPassword, updateProfile } from 'firebase/auth';
import { doc, setDoc } from 'firebase/firestore';
import { useNavigate, Link } from 'react-router-dom';
import './AuthPage.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      navigate('/complete-profile');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h1>‚ö° BrainBlitz</h1>
          <h2>¬°√önete a la diversi√≥n!</h2>
          <p>Crea tu cuenta y comienza a jugar</p>
        </div>

        <form onSubmit={handleRegister} className="auth-form">
          <div className="input-group">
            <input 
              type="email" 
              placeholder="Correo electr√≥nico" 
              value={email} 
              onChange={e => setEmail(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>
          <div className="input-group">
            <input 
              type="password" 
              placeholder="Contrase√±a" 
              value={password} 
              onChange={e => setPassword(e.target.value)} 
              required 
              disabled={loading}
            />
          </div>
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}
          <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
            {loading ? 'Creando cuenta...' : 'Crear cuenta'}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            ¬øYa tienes cuenta? <Link to="/login">Inicia sesi√≥n aqu√≠</Link>
          </p>
        </div>
      </div>
    </div>
  );
}
